import type { ComponentsUnion } from '@sendbird/uikit-message-template';

import { useMessageContext } from '../context/MessageContextProvider';

/**
 * This hook is used to highlight each item in the message template by extending its style.
 * */
export function useHighlightStyle(raw: ComponentsUnion['properties']) {
  const { highlight } = useMessageContext();

  return {
    className: highlight?.getContainerClassName ? highlight.getContainerClassName(raw) : undefined,
    style: highlight?.getContainerInlineStyle ? highlight.getContainerInlineStyle(raw) : undefined,
    renderText: highlight?.renderText ? highlight.renderText : (it: string) => it,
  };
}
